mapper.select:
SELECT * FROM (
SELECT ROWNUM rnum, t1.* FROM (
SELECT d.store_order_detail_no, d.invoice_no, d.delivery_date, e.emp_name, e.emp_code, t1.branch_name FROM Delivery d, Store_order_detail s, Employee e, Branch b, ( SELECT d.store_order_detail_no, b.branch_no, b.branch_name FROM Store_order_detail d, Store_order_reservation r, Employee e, Branch b WHERE d.store_order_res_code = r.store_order_res_code AND e.emp_code = r.emp_code AND b.branch_no = e.branch_no
AND b.branch_no = #{branch_no}
) t1 WHERE d.store_order_detail_no = s.store_order_detail_no AND d.emp_code = e.emp_code AND e.branch_no = b.branch_no AND e.branch_no = #{session_branch_no}
AND d.store_order_detail_no = #{store_order_detail_no}
AND d.invoice_no = #{invoice_no}
AND d.delivery_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD')
ORDER BY d.delivery_no DESC) t1) t2
WHERE t2.rnum >= (#{currPage}-1)*5+1 AND t2.rnum <= #{currPage}*5

SELECT COUNT(*) FROM Delivery d, Store_order_detail s, Employee e, Branch b, ( SELECT d.store_order_detail_no, b.branch_no, b.branch_name FROM Store_order_detail d, Store_order_reservation r, Employee e, Branch b WHERE d.store_order_res_code = r.store_order_res_code AND e.emp_code = r.emp_code AND b.branch_no = e.branch_no
AND b.branch_no = #{branch_no}
) t1 WHERE d.store_order_detail_no = s.store_order_detail_no AND d.emp_code = e.emp_code AND e.branch_no = b.branch_no AND e.branch_no = #{session_branch_no}
AND d.store_order_detail_no = #{store_order_detail_no}
AND d.invoice_no = #{invoice_no}
AND d.delivery_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD')
