mapper.select:
SELECT * FROM( SELECT ROWNUM rnum, t1.* FROM ( SELECT f.factory_order_code, f.factory_order_res_no, f.emp_code, e.emp_name, e.branch_no, f.factory_order_res_date FROM Factory_order f, Employee e, Branch b WHERE f.emp_code = e.emp_code AND e.branch_no = b.branch_no AND b.branch_no = #{branch_no}
ORDER BY f.factory_order_code DESC
ORDER BY e.emp_name
ORDER BY f.factory_order_res_date DESC
) t1 ) t2
WHERE t2.rnum >= (#{currPage}-1)*5+1 AND t2.rnum <= #{currPage}*5

SELECT * FROM( SELECT ROWNUM r, t1.* FROM( SELECT f.factory_order_code, f.factory_order_res_no, f.emp_code, e.emp_name, e.branch_no, f.factory_order_res_date FROM Factory_order f, Employee e, Branch b WHERE f.emp_code = e.emp_code AND e.branch_no = b.branch_no AND b.branch_no = #{branch_no}
AND f.factory_order_res_no = #{factory_order_res_no}
AND f.emp_code = #{emp_code}
AND f.factory_order_res_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD')
ORDER BY f.factory_order_code DESC
ORDER BY e.emp_name
ORDER BY f.factory_order_res_date DESC
) t1) t2
WHERE t2.r >= (#{currPage}-1)*5+1 AND t2.r <= #{currPage}*5

select
factory_order.*
from supplier, factory_order, factory_order_detail, Product_component, branch, employee where factory_order_detail.comp_no = product_component.comp_no and factory_order_detail.comp_no = supplier.comp_no and supplier.comp_no = product_component.comp_no and branch.emp_code = factory_order.emp_code and employee.emp_code = branch.emp_code and factory_order.factory_order_code = factory_order_detail.factory_order_code and supplier.supplier_no = factory_order_detail.supplier_no and factory_order.emp_code=#{emp_code} and factory_order.factory_order_res_no=#{factory_order_res_no} and factory_order_detail.factory_order_detail_no=#{factory_order_detail_no}

 SELECT COUNT(*) 
 FROM Factory_order f, Employee e, Branch b
WHERE f.emp_code = e.emp_code
AND e.branch_no = b.branch_no
AND b.branch_no = #{branch_no}

  AND f.factory_order_res_no = #{factory_order_res_no}


  AND f.emp_code = #{emp_code}


  AND f.factory_order_res_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD')
