mapper.select:
SELECT * FROM( SELECT ROWNUM rnum, t1.* FROM ( SELECT o.factory_out_item_code, o.factory_out_item_no, o.product_no, o.factory_out_item_qty, o.emp_code, o.factory_out_item_date, o.branch_no FROM Factory_out_item o, Employee e WHERE o.emp_code = e.emp_code AND e.branch_no = #{branch_no}
ORDER BY o.factory_out_item_code DESC
ORDER BY o.factory_out_item_qty DESC
ORDER BY o.factory_out_item_date DESC
) t1 ) t2
WHERE t2.rnum >= (#{currPage}-1)*5+1 AND t2.rnum <= #{currPage}*5

SELECT * FROM(
SELECT ROWNUM rnum, t1.* FROM(
SELECT f.factory_out_item_no, p.product_name, f.factory_out_item_qty, e.emp_name, f.factory_out_item_date, b.branch_name, b.branch_no, p.product_no, e.emp_code FROM Factory_out_item f, Product p, Employee e, Branch b WHERE f.emp_code = e.emp_code AND f.branch_no = b.branch_no AND f.product_no = p.product_no
AND f.factory_out_item_no = #{factory_out_item_no}
AND p.product_no = #{product_no}
AND b.branch_no = #{branch_no}
AND f.factory_out_item_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD')
AND e.branch_no = #{session_branch_no} ORDER BY f.factory_out_item_code DESC
ORDER BY o.factory_out_item_qty DESC
ORDER BY o.factory_out_item_date DESC
) t1) t2
WHERE t2.rnum >= (#{currPage}-1)*5+1 AND t2.rnum <= #{currPage}*5

SELECT COUNT(*)
FROM Factory_out_item f, Product p, Employee e, Branch b
        WHERE f.emp_code = e.emp_code
        AND f.branch_no = b.branch_no
        AND f.product_no = p.product_no

        AND f.factory_out_item_no = #{factory_out_item_no}


        AND p.product_no = #{product_no}


        AND b.branch_no = #{branch_no}


  	  AND f.factory_out_item_date BETWEEN TO_DATE(#{start_date},'YYYY-MM-DD') AND TO_DATE(#{end_date},'YYYY-MM-DD') 

        AND e.branch_no = #{session_branch_no}
