mapper.update:

UPDATE Freeboard 
SET freeboard_title = #{freeboard_title}, freeboard_content = #{freeboard_content}
WHERE freeboard_no = #{freeboard_no}


UPDATE Freeboard 
SET freeboard_view = freeboard_view + 1
WHERE freeboard_no = #{freeboard_no}

mapper.delete:

DELETE
FROM Freeboard
WHERE freeboard_no = #{freeboard_no}
AND emp_code = #{emp_code}

mapper.select:

SELECT *
 FROM Freeboard
WHERE freeboard_no = #{freeboard_no}
AND emp_code = #{emp_code}
